		set newPrimaryKeyValue = ""
		if $length(%primaryKeyNames, ",") = 2 {
			set newPrimaryKeyValue = $$^WWWNEXT(%tableName)
		} elseif $length(%primaryKeyNames, ",") > 2 {
			set primaryKeyValues = ""
