			set newPrimaryKeyValue = $$^WWWNEXT1(%tableName, primaryKeyValues, $length(%primaryKeyNames, ",") - 1)
		}
		set $ZObjProperty(##this, $piece(%primaryKeyNames, ",", $length(%primaryKeyNames, ","))) = newPrimaryKeyValue
	}

	if formDefinition '= "" {
		set entryDateField = ""
		set enteredByField = ""
