Method.Implementation:
// Execute only if you load INLP without $$$SAVE command
$$$VAR
set count = 0 set loc = $order(^INLP(0, "")) while ($length(loc) > 0) { set id = $order(^INLP(0, loc, ""))
while ($length(id) > 0) { set objINLP = $get(^INLP(0, loc, id, 1)) set idINLP = loc_","_id set status = $$Save^COMUtils("INLP", idINLP, objINLP, 1) write !, "status = "_status set count = count + 1
set id = $order(^INLP(0, loc, id)) }
set loc = $order(^INLP(0, loc)) }
write !, "Total = "_count

// Rotina para limpar algumas globais que foram feitos testes no PROD // e também para zerar os numeradores dessas globais e outras // que não tiveram os numeradores zerados
$$$VAR
// Limpar as globais: kill ^INReq, ^INReqs kill ^INReqLine, ^INReqLines kill ^INIssue, ^INIssues kill ^INIssueset, ^INIssuesets kill ^INIssueLine, ^INIssueLines kill ^INDRPDEMAND, ^INDRPDEMANDs kill ^INDRPSUPPLY, ^INDRPSUPPLYs
kill ^INAUFPb kill ^INAUFPbi kill ^INAUFb kill ^INAUFbi
// Zerar os numeradores: set ^WWW128(YM, "INMOV", 1) = "YEAR/000000" set ^WWW128(YM, "INReq", 1) = "YEAR/000000" set ^WWW128(YM, "INIssue", 1) = "EXYEAR/000000" set ^WWW128(YM, "INAUF", 1) = "YEAR/000000" set ^WWW128(YM, "INADJ", 1) = "YEAR/000000" set ^WWW128(YM, "INDRPDEMAND", 1) = "YEAR/000000" set ^WWW128(YM, "INDRPSUPPLY", 1) = "YEAR/000000"

set globalName = ##class(Global).GetNextGlobalName($znspace, "")
while ($length(globalName) > 0) {
	if (($extract(globalName, 1, $length("^%")) '= "^%") &&
		($extract(globalName, 1, $length("^Cache")) '= "^Cache") &&
		($extract(globalName, 1, $length("^SourceControl")) '= "^SourceControl") &&
		($extract(globalName, 1, $length("^x")) '=
			##class(Strings).LowerCase($extract(globalName, 1, $length("^x")))) &&
		$data(@globalName)) {
		write !, "globalName = "_globalName
	}

	set globalName = ##class(Global).GetNextGlobalName($znspace, globalName)
}
