for intCounter=(intCounter+1):1:intCounts { set intField = intField+1 set $$$WWW122ManualCaption(objWWW122) = "Count "_intCounter set $$$WWW122DefaultVariableInput(objWWW122) = "@$$DefaultVariableInput^INWEINVELINE(YBBN,"_intCounter_")" set $$$WWW122ExecuteBeforeDataField(objWWW122) = "do OnBeforeFormConstruction^INWEINVELINE(YKEY,YBBN,"_intCounter_")"
set strKey = "INWEINVELINE"_$$$COMMA_intField set strStatus = $$$Save("WWW122",$$^WWWKEYBUILD(strKey),objWWW122,$$$YES)
set strStatus = $$DynamicallyAddCountLang(strKey,intField,intCounter) set strStatus = $$DynamicallyAddCountCust(strKey,intField,intCounter) } quit
