; If new lines have been added added, check required bundle attributes have been provided set objItem = ##class(alINV.iUINV).GetItem($$$INWEINVItemNumber(pYFELD))
if objItem '= $$$NULLOREF { //SRAdhoc 08-Feb-2011 if objItem.ItemGroup'="" { set strStatus = $$ApplyGroupFilter(idStocktake,objItem.ItemGroup.%Id()) if $$$ISERR(strStatus) set YRETVAL = strStatus }
; SR17060 vvvvv if $$$ISOK(strStatus) { set strStatus = $$ApplyStoreFilter(idStocktake,$$$INWEINVStockLocation(pYFELD)) if $$$ISERR(strStatus) set YRETVAL = strStatus }
if $$$ISOK(strStatus) { set strStatus = $$ApplyLocnFilter(idStocktake,$$$INWEINVLocation(pYFELD)) if $$$ISERR(strStatus) set YRETVAL = strStatus } ; SR17060-2 ^^^^
if $$$ISOK(strStatus) { // set strStatus = $$ValidateAttributes(objItem,pYFELD,$$$YES) set strStatus = $$ValidateAttributes($$$INWEINVItemNumber(pYFELD),pYFELD,$$$YES,pYKEY) if $$$ISERR(strStatus) set YRETVAL = strStatus }
