if $$$INWEINVCountQuantity(objINWEINVCount)'=$$$INWEINVCountQuantity(objINWEINVCountOLD) { set $$$INWEINVCountTimestamp(pYFELD) = $horolog set $$$INWEINVCountTimestamp1(objINWEINVCount) = $horolog }
